function multipleOf(multiple, n) {
    return multiple * Math.ceil(n / multiple);
}

class Range {
    /**
    * @param {number} min
    * @param {number} max
    */
    constructor(min, max) {
        this.min = min;
        this.max = max;
    }

    /**
    * @param {number} value
    */
    clamp(value) {
        return Math.min(Math.max(value, this.min), this.max);    
    }

    /**
    * @param {number} t
    */
    lerp(t) {
        return this.min + t * (this.max - this.min);
    }

    /**
    * @param {number} value
    */
    invLerp(value) {
        return (value - this.min) / (this.max - this.min);
    }
}

/**
* @param {Range} input
* @param {Range} output
* @param {number} value
*/
function remap(input, output, value) {
    const t = input.invLerp(value);
    return output.lerp(t);
}

class Result {
    constructor() {
        this.power = 0.0;
        this.reason = [];
    }

    /**
    * @param {number} power
    * @param {string} reason
    */
    push(power, reason) {
        this.power = Math.max(this.power, power);
        this.reason.push(reason);
    }
}

const PpmRange = new Range(400, 700);
const PowerRange = new Range(0.0, 1.0);
const LowHumidityPowerRange = new Range(0.0, 0.7);
const LowHumidity = 40;

/**
* @param {{ manualOverride: any; ppm: number; cookerHood: any; ventilationSwitch: any; downstairsHumidity: number; bathroomMotion: any; downstairsBathroomMotion: any; }} payload
*/
function getPowerFromPayload(payload) {
    const result = new Result();

    if (payload.manualOverride) {
        result.push(1.0, "manual override");
    }

    if (payload.ppm > PpmRange.max) {
        result.push(1.0, `CO\u2082 > ${PpmRange.max} ppm`);
    }

    if (payload.rangeHood) {
        result.push(1.0, "range hood fan on");
    }

    if (payload.ventilationSwitch) {
        result.push(1.0, "ventilation switch");
    }

    const isLowHumidity = (payload.downstairsHumidity < LowHumidity);
    const powerRange = isLowHumidity
        ? LowHumidityPowerRange
        : PowerRange;

    if (isLowHumidity) {
        result.push(
            0.0, 
            `humidity ${payload.downstairsHumidity} % (< ${LowHumidity} %)`
        );
    }

    if (payload.bathroomMotion) {
        result.push(powerRange.max, "bathroom motion");
    }

    if (payload.downstairsBathroomMotion) {
        result.push(powerRange.max, "downstairs bathroom motion");
    }

    result.push(
        remap(PpmRange, powerRange, PpmRange.clamp(payload.ppm)),
        `CO\u2082 ${payload.ppm} ppm`
    );

    return result;
}

const source = msg.payload;
let { power, reason } = getPowerFromPayload(source);

return {
    payload: new Range(10, 100).clamp(multipleOf(1, 100 * power)),
    reason: reason.join(", "),
    source
};

function C(value) {
    return `${value} \u00B0C`;
}

function reasonObject(result, reason) {
    return { result, reason };
}

function determine(fresh, stale, override) {
    const Cold = 18;
    const Hot = 23;

    if (override == 'Force On') {
        return reasonObject(true, 'Override');
    }

    if (override == 'Force Off') {
        return reasonObject(false, 'Override');
    }

    if (fresh < Cold) {
        return reasonObject(
            true,
            `Fresh air temperature lower than ${C(Cold)}.`
        );
    }

    if (fresh >= Hot) {
        const reason = `Fresh air temperature greater than ${C(Hot)}`
        if (stale < fresh) {
            return reasonObject(
                true,
                reason + ' and stale air temperature is lower.'
            );
        } else {
            return reasonObject(
                false,
                reason + ', but stale air temperature is greater.'
            );
        }
    }

    return reasonObject(
        false,
        `Fresh air temperature greater than ${C(Cold)} and lower than ${C(Hot)}.`
    );
}

const fresh = parseFloat(msg.payload.fresh);
const stale = parseFloat(msg.payload.stale);
const override = msg.payload.override;

const payload = determine(fresh, stale, override);

if (payload.result) {
    return [{ payload }, null];
} else {
    return [null, { payload }];
}

const DefaultMinTemperature = 18.0;
const DefaultMaxTemperature = 28.0;

/**
* @param {string} topic
* @param {string} suffix
* @param {object} payload
* @returns {Number | undefined}

*/
function getTemperature(topic, suffix, payload) {
    const anyoneHome = payload['binary_sensor.anyone_home']
    if (anyoneHome === 'off') {
        return undefined;
    }
    const key = `input_number.${topic}_${payload.timeOfDay}_temperature_${suffix}`;
    const result = key in payload ? parseFloat(payload[key]) : undefined
    return result;
}

/**
* @param {string} topic
* @param {Object} payload
*/
function getTemperatureRange(topic, payload) {
    return {
        topic,
        payload: {
            minTemperature: getTemperature(topic, 'min', payload) ?? DefaultMinTemperature,
            maxTemperature: getTemperature(topic, 'max', payload) ?? DefaultMaxTemperature,
        }
    };
}

const { payload } = msg;
const { timeOfDay } = payload;
if (!timeOfDay) {
    return [[]];
}
return [
    [
        getTemperatureRange('upstairs', payload),
        getTemperatureRange('downstairs', payload),
    ]
];

class Time {

    /** @type {number} */ hours;
    /** @type {number} */ minutes;

    /**
    * @param {number} hours
    * @param {number} minutes
    */
    constructor(hours, minutes) {
        this.hours = hours;
        this.minutes = minutes;
    }

    valueOf() {
        return this.hours * 60 + this.minutes;
    }

    toString() {
        /** @param {number} num */
        function nn(num) {
            const str = num.toString();
            return str.length === 1 ? "0" + str : str;
        }
        return `${nn(this.hours)}:${nn(this.minutes)}`;
    }

    /**
    * @param {Date} date
    */
    static fromDate(date) {
        return new Time(date.getHours(), date.getMinutes());
    }

    /**
    * @param {string} str
    */
    static fromString(str) {
        const regexp = /^(\d\d):(\d\d):(\d\d)$/;
        const [_, hours, minutes] = regexp.exec(str);
        return new Time(parseInt(hours, 10), parseInt(minutes, 10));
    }
}

/**
* @param {Date} date
*/
function isWeekend(date) {
    const day = date.getDay();
    return day === 0 || day === 6;
}

const DefaultMorningStart = new Time(5, 0);
const DefaultDayStartWeek = new Time(9, 0);
const DefaultDayStartWeekend = new Time(10, 30);
const DefaultNightStart = new Time(21, 0);

class TimeConfiguration {
    /**
    * @param {{ [x: string]: string; }} payload
    */
    constructor(payload){
        /**
        * @param {string} key
        * @param {Time} ifMissing
        */
        function getTimeFromPayload(key, ifMissing) {
            const str = payload[key];
            return str ? Time.fromString(str) : ifMissing;
        }
        this.morningStart = getTimeFromPayload(
            "input_datetime.morning_start",
            DefaultMorningStart
        );
        this.dayStartWeek = getTimeFromPayload(
            "input_datetime.day_start_week",
            DefaultDayStartWeek
        );
        this.dayStartWeekend = getTimeFromPayload(
            "input_datetime.day_start_weekend",
            DefaultDayStartWeekend
        );
        this.nightStart = getTimeFromPayload(
            "input_datetime.night_start_weekend",
            DefaultNightStart
        );
    }

    /**
    * @param {Date} date
    */
    dayStart(date) {
        if (isWeekend(date)) {
            return this.dayStartWeekend;
        } else {
            return this.dayStartWeek;
        }
    }
}


/**
* @param {object} payload
*/
function getTimeOfDay(payload) {
    const timestamp = parseInt(payload.timestamp, 10);
    const date = new Date(timestamp);
    const time = Time.fromDate(date);
    const config = new TimeConfiguration(payload);

    if (time < config.morningStart) {
        return "night";
    } else if (time < config.dayStart(date)) {
        return "morning"
    } else if (time < config.nightStart) {
        return "day";
    } else {
        return "night";
    }
}

return {
    payload: {
        ...msg.payload,
        timeOfDay: getTimeOfDay(msg.payload)
    }
};

/**
* @param {string} str
* @returns {number}
*/
function parseHHMM(str) {
    const regex  = /^(\d{1,2}):(\d{1,2})$/
    const matches = regex.exec(str);
    if (!matches) {
        return undefined;
    }
    return parseInt(matches[1], 10) * 60 + parseInt(matches[2], 10);
}

/**
 * @typedef {'open'|'closed'}              CoverState
 * @typedef {[string, CoverState]}         CoverStateEntry
 * @typedef {{[key: string]: CoverState}}  CoverStateMap
 */

/**
 * @template T
 * @param {[string, T][]} newEntries
 * @param {{[key: string]:T}} lastObject
 * @returns {{ [key: string]: T }}
*/
function keepOnlyChanges(newEntries, lastObject) {
    return newEntries.filter(
        ([key, newValue]) => newValue !== lastObject[key]
    );
}

const belowHorizon = msg.payload.sun == 'below_horizon';
const currentTime = parseHHMM(msg.payload.time);

/** @type { [string, string][] } */
const settings = Object.entries(msg.payload.settings);

/** @type {CoverStateEntry[]} */
const newEntries = [];

for (const [cover, setting] of settings) {
    if (belowHorizon) {
        newEntries.push([cover, 'closed']);
    } else if (setting == 'Sunrise') {
        newEntries.push([cover, 'open']);
    } else if (setting !== 'Never') {
        const settingTime = parseHHMM(setting);
        newEntries.push([
            cover, 
            currentTime >= settingTime ? 'open' : 'closed'
        ]);
    }
}

/** @type {CoverStateMap} */
const lastState = flow.get('lastState') || {};
flow.set('lastState', Object.fromEntries(newEntries));

const changedEntries = keepOnlyChanges(newEntries, lastState);
return changedEntries.length === 0 
    ? [] 
    : [{ payload: Object.fromEntries(changedEntries) }];
